// client/src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import AdminDashboard from './components/AdminDashboard';
import Login from './components/Login';

// In App.js tempor채r zum Testen hinzuf체gen
function App() {
  console.log("App rendering"); // Debug-Ausgabe

  return (
    <div className="App" style={{backgroundColor: 'white', color: 'black'}}>
      {/* Direktes Inline-Styling zum Testen */}
      <h1 style={{color: 'red'}}>Test Heading</h1>
      <p style={{color: 'blue'}}>Test Paragraph</p>

      <Router>
        {/* Rest des Codes... */}
        <Routes>
          <Route path="/login" element={
            isAuthenticated ? 
              <Navigate to="/admin" /> : 
              <Login setIsAuthenticated={setIsAuthenticated} />
          } />
          <Route 
            path="/admin" 
            element={
              isAuthenticated ? 
                <AdminDashboard setIsAuthenticated={setIsAuthenticated} /> : 
                <Navigate to="/login" />
            } 
          />
          <Route path="/" element={<Navigate to={isAuthenticated ? "/admin" : "/login"} />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
